(51, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-08-25 22:24:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-09-04 22:46:44', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-25 22:24:55', NULL, 0, 'plain', '', 0, 1, 'info'),
(62, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'actioned', 'woocommerce-admin', '2023-08-28 19:02:11', NULL, 0, 'plain', '', 0, 1, 'info'),
(63, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-28 19:02:11', NULL, 0, 'plain', '', 0, 1, 'info'),
(64, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-28 19:02:11', NULL, 0, 'plain', '', 0, 1, 'info'),
(65, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2023-09-04 19:06:43', NULL, 0, 'plain', '', 0, 1, 'info'),
(66, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://localhost/franca/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Read more</a> about what data we collect.', '{}', 'unactioned', 'woocommerce-admin', '2023-09-04 19:06:43', NULL, 0, 'plain', '', 0, 1, 'info'),
(67, 'tiktok_shopGTM_q3-2023_hasTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-07 12:23:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'tiktok_shopGTM_q3-2023_noTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-07 23:47:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'wc-admin-launch-checklist', 'info', 'en_US', '¿Estás preparado para lanzar tu tienda?', 'Para asegurarnos de que nunca tengas esa sensación de «¿Qué se me olvidó?» hemos preparado la lista de comprobación esencial de despegue.', '{}', 'unactioned', 'woocommerce-admin', '2023-10-02 20:09:04', NULL, 0, 'plain', '', 0, 1, 'info'),
(70, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-10-02 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'wc-update-db-reminder', 'update', 'en_US', 'Actualización de la base de datos de WooCommerce terminada', 'Actualización de la base de datos de WooCommerce completa. ¡Gracias por actualizar a la última versión!', '{}', 'unactioned', 'woocommerce-core', '2023-10-02 20:15:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'Klarna_12OCT23_B', 'marketing', 'en_US', 'Enable buy now, pay later without additional extensions', 'Klarna has just been added directly into WooPayments — and you’re invited to be one of the first stores to activate it. <br><br>With one click in your dashboard, you can give customers a flexible buy now, pay later option that can increase your average order value and boost conversions.', '{}', 'pending', 'woocommerce.com', '2023-10-12 22:02:03', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'wc_com_in_person_payments_card_sale_US_inbox_note_q4_23', 'marketing', 'en_US', 'Save 35% on a WooPayments card reader', 'Have you been thinking about selling your products or services in person? Now’s the perfect time to do so, as we’re discounting our M2 card readers by 35%! Securely process payments wherever your business takes you, with full store and inventory sync. Don’t miss out — this offer ends October 29, 2023.', '{}', 'pending', 'woocommerce.com', '2023-10-13 21:49:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'wc_com_in_person_payments_card_sale_UK_inbox_note_q4_23', 'marketing', 'en_US', 'Save 35% on a WooPayments card reader', 'Have you been thinking about selling your products or services in person? Now’s the perfect time to do so, as we’re discounting our WisePad 3 card readers by 35%! Securely process payments wherever your business takes you, with full store and inventory sync. Don’t miss out — this offer ends 29th October 2023.', '{}', 'pending', 'woocommerce.com', '2023-10-13 21:49:42', NULL, 0, 'plain', '', 0, 0, 'info'),
